import React, { useState } from "react";
import './Food.css';

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    } else {
      alert("No file selected. Please choose an image.");
    }
  };

  const handleCameraCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const videoElement = document.createElement('video');
      videoElement.srcObject = stream;
      videoElement.play();

      const canvas = document.createElement('canvas');
      const context = canvas.getContext('2d');

      videoElement.addEventListener('canplay', () => {
        canvas.width = videoElement.videoWidth;
        canvas.height = videoElement.videoHeight;
        context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
        canvas.toBlob((blob) => {
          setImage(blob);
          setImagePreview(canvas.toDataURL('image/jpeg'));
          stream.getTracks().forEach(track => track.stop());
        }, 'image/jpeg');
      });
    } catch (error) {
      console.error("Error accessing camera:", error);
      alert("Unable to access camera. Please check permissions.");
    }
  };

  const getLocation = () => {
    return new Promise((resolve, reject) => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(resolve, reject);
      } else {
        reject(new Error("Geolocation is not supported by this browser."));
      }
    });
  };

  const getCityFromCoordinates = async (latitude, longitude) => {
    try {
      const response = await fetch(
        `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${latitude}&longitude=${longitude}&localityLanguage=en`
      );
      const data = await response.json();
      return data.city || "Unknown City";
    } catch (error) {
      console.error("Error fetching city:", error);
      return "Unknown City";
    }
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image first.");
      return;
    }

    try {
      // Get location information
      const location = await getLocation();
      const { latitude, longitude } = location.coords;
      const city = await getCityFromCoordinates(latitude, longitude);

      // Upload the image to Cloudinary
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", "food123");

      const uploadResponse = await fetch(`https://api.cloudinary.com/v1_1/dmf3jzijx/image/upload`, {
        method: "POST",
        body: formData,
      });

      if (!uploadResponse.ok) {
        throw new Error("Image upload failed. Unable to create a valid link.");
      }

      const uploadData = await uploadResponse.json();

      // Validate if a proper link is returned
      const imageUrl = uploadData.secure_url;
      if (!imageUrl || !imageUrl.startsWith("http")) {
        throw new Error("Invalid image URL generated. Please try again.");
      }

      // Prepare JSON payload for FastAPI
      const payload = {
        image: imageUrl,
        location: city,
      };

      // Log the request payload
      console.log("Request payload to FastAPI:", payload);

      // Call FastAPI for food analysis
      const response = await fetch("https://foodapi-sa0l.onrender.com/analyze-food", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        if (response.status === 404) {
          throw new Error("The analysis endpoint was not found. Please check the backend URL.");
        } else if (response.status === 500) {
          throw new Error("The server encountered an error. Please try again later.");
        } else {
          throw new Error("Unexpected server error.");
        }
      }

      const data = await response.json();

      if (!data.result) {
        throw new Error("Analysis failed. No result returned.");
      }

      setResult({
        status: data.result,
        daysUntilSpoilage: data.daysUntilSpoilage,
      });

      // Clear any previous error
      setError(null);

    } catch (error) {
      console.error("Error during upload or analysis:", error);

      if (error.message.includes("Image upload failed")) {
        setError("Error uploading the image. Please ensure the image is valid and try again.");
      } else if (error.message.includes("Invalid image URL")) {
        setError("Image link generation failed. Please try uploading the image again.");
      } else if (error.message.includes("Geolocation")) {
        setError("Unable to fetch location. Please ensure location services are enabled.");
      } else if (error.message.includes("Failed to analyze")) {
        setError("Error analyzing food. Please try again.");
      } else if (error.message.includes("The analysis endpoint")) {
        setError("Analysis service is unavailable. Please contact support.");
      } else {
        setError("An unknown error occurred. Please try again.");
      }

      setResult({
        status: "Error during analysis.",
        daysUntilSpoilage: null,
      });
    }
  };

  const resetAnalysis = () => {
    setImage(null);
    setImagePreview(null);
    setResult(null);
    setError(null);
  };

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. Our AI ensures quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
        <button className="analyze-button" onClick={handleCameraCapture}>üì∑ Capture from Camera</button>
      </div>
      <button className="analyze-button analyze-large" onClick={analyzeFood}>üîç Analyze</button>
      {error && <p className="error-message">{error}</p>}
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img className="image-preview-large" src={imagePreview} alt="Analyzed food" />
          </div>
          <div className="result-container">
            <p className="result-text">üß™ Result: {result.status}</p>
            {result.daysUntilSpoilage && (
              <p className="result-text">‚åõ Days Until Spoilage: {result.daysUntilSpoilage}</p>
            )}
          </div>
        </div>
      )}
      <button className="reset-button" onClick={resetAnalysis}>üîÑ Reset</button>
    </div>
  );
};

export default Food;
