import React, { useState, useEffect } from "react";
import './Food.css';
import { initializeApp } from 'firebase/app';
import { getStorage, ref, uploadBytes, getDownloadURL, deleteObject } from 'firebase/storage';
import { getDatabase, ref as dbRef, set } from 'firebase/database';


// Initialize Firebase (Replace with your Firebase config)
const firebaseConfig = {
  apiKey: "AIzaSyB-L2pj_4XVH9y7VBYOSAsTsT8p4cveHZY",
  authDomain: "foodai-7ebf0.firebaseapp.com",
  databaseURL: "https://foodai-7ebf0-default-rtdb.firebaseio.com",
  projectId: "foodai-7ebf0",
  storageBucket: "foodai-7ebf0.firebasestorage.app",
  messagingSenderId: "1802738846",
  appId: "1:1802738846:web:f6985b95b8487ce3c7ef8f",
  measurementId: "G-QJWXCZ7Y56"
};
if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [imagePath, setImagePath] = useState(null); // Store the path to delete from Firebase
  const [result, setResult] = useState(null);
  const [location, setLocation] = useState(null);

  // Handle image upload and preview
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Reset the form and delete the image from Firebase
  const resetAnalysis = async () => {
    // Delete the uploaded image from Firebase Storage if path exists
    if (imagePath) {
      try {
        const storageRef = firebase.storage().ref(imagePath);
        await storageRef.delete();
        console.log("Image deleted from Firebase.");
      } catch (error) {
        console.error("Error deleting image from Firebase:", error);
      }
    }

    // Reset state variables
    setImage(null);
    setImagePreview(null);
    setImagePath(null);
    setResult(null);
    setLocation(null);

    fetchLocationCity(); // Fetch location again for new analysis
  };

  // Capture user's city name using Geolocation
  const fetchLocationCity = async () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;

          try {
            const response = await fetch(
              `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lon}&localityLanguage=en`
            );
            const data = await response.json();
            setLocation(data.city || "Unknown City");
          } catch (error) {
            console.error("Error fetching city:", error);
            setLocation("Unknown City");
          }
        },
        (error) => {
          console.error("Error fetching location:", error);
          alert("Unable to fetch location. Please enable location services.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser.");
    }
  };

  // Upload image and analyze food
  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload an image first.");
      return;
    }

    if (!location) {
      alert("Fetching location. Please try again in a moment.");
      return;
    }

    const storageRef = firebase.storage().ref();
    const filePath = `images/${Date.now()}_${image.name}`;
    const fileRef = storageRef.child(filePath);

    try {
      // Upload image to Firebase Storage
      const snapshot = await fileRef.put(image);
      const downloadURL = await snapshot.ref.getDownloadURL();

      // Save the file path for deletion on reset
      setImagePath(filePath);

      // Save the image URL and location to Firebase Realtime Database
      const db = firebase.database();
      await db.ref("foodImages").push({
        imageUrl: downloadURL,
        location: location,
      });

      // Send the image URL and location city to FastAPI
      const response = await fetch("https://foodapi-sa0l.onrender.com", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          image_url: downloadURL,
          location: location,
        }),
      });

      const data = await response.json();

      // Extract only the required details from the response
      const isExpired = new Date(data.meta_data.exp_date) < new Date();
      const daysToExpire = isExpired
        ? 0
        : Math.ceil(
            (new Date(data.meta_data.exp_date) - new Date()) /
            (1000 * 60 * 60 * 24)
          );

      setResult({
        isExpired,
        daysToExpire,
      });
    } catch (error) {
      console.error("Error analyzing food:", error);
      setResult({
        isExpired: null,
        daysToExpire: null,
      });
    }
  };

  // Fetch location on component mount
  useEffect(() => {
    fetchLocationCity();
  }, []);

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. 
        Our AI ensures quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
        <button className="analyze-button" onClick={analyzeFood}>üîç Analyze</button>
        <button className="reset-button" onClick={resetAnalysis}>üîÑ Reset</button>
      </div>
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img className="image-preview-large" src={imagePreview} alt="Analyzed food" />
          </div>
          <div className="result-container">
            {result.isExpired !== null && (
              <>
                <p className="result-text">
                  {result.isExpired
                    ? "‚ö†Ô∏è The food is expired."
                    : `‚úÖ The food is fresh. Days until expiry: ${result.daysToExpire}`}
                </p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Food;
