import React, { useState } from "react";
import "./Food.css";

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    } else {
      alert("Please choose an image.");
    }
  };

  const handleCameraCapture = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      const video = document.createElement("video");
      video.srcObject = stream;
      video.play();

      const canvas = document.createElement("canvas");
      const context = canvas.getContext("2d");

      video.addEventListener("canplay", () => {
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0, canvas.width, canvas.height);
        canvas.toBlob((blob) => {
          setImage(blob);
          setImagePreview(canvas.toDataURL("image/jpeg"));
          stream.getTracks().forEach((track) => track.stop());
        }, "image/jpeg");
      });
    } catch {
      alert("Unable to access camera.");
    }
  };

  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload or capture an image.");
      return;
    }
    try {
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", "food123");

      const uploadRes = await fetch(
        `https://api.cloudinary.com/v1_1/dmf3jzijx/image/upload`,
        { method: "POST", body: formData }
      );
      const uploadData = await uploadRes.json();

      const payload = {
        image: uploadData.secure_url,
        location: "Unknown",
      };

      const res = await fetch(
        "https://foodapi-sa0l.onrender.com/direct_img",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(payload),
        }
      );
      const data = await res.json();
      setResult(data.meta_data);
    } catch {
      setError("Failed to analyze food.");
    }
  };

  const resetAnalysis = () => {
    setImage(null);
    setImagePreview(null);
    setResult(null);
    setError(null);
  };

  return (
    <div className="food-container">
      <h1 className="food-title">AI Food Analyzer</h1>
      <p>Upload or capture food photos to check for spoilage.</p>
      <div className="upload-container">
        <input type="file" onChange={handleImageUpload} />
        <button onClick={handleCameraCapture}>üì∑ Capture</button>
      </div>
      <button onClick={analyzeFood}>üîç Analyze</button>
      {error && <p className="error">{error}</p>}
      {result && (
        <div className="result">
          <img src={imagePreview} alt="Preview" />
          <ul>
            <li>üìã Category: {result.category}</li>
            <li>üçå Type: {result.type}</li>
            <li>üå°Ô∏è Temperature: {result.temperature}¬∞C</li>
            <li>üíß Humidity: {result.humidity}%</li>
            <li>üìÖ Expiry: {result.exp_date}</li>
          </ul>
        </div>
      )}
      <button onClick={resetAnalysis}>üîÑ Reset</button>
    </div>
  );
};

export default Food;
