import React, { useState, useEffect } from "react";
import './Food.css';
import axios from 'axios';

const CLOUDINARY_UPLOAD_URL = "https://api.cloudinary.com/v1_1/dmf3jzijx/image/upload";
const CLOUDINARY_UPLOAD_PRESET = "food123"; // Your upload preset
const FAST_API_URL = "https://foodapi-sa0l.onrender.com"; // FastAPI endpoint

const Food = () => {
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [imageUrl, setImageUrl] = useState(null); // URL from Cloudinary
  const [result, setResult] = useState(null);
  const [location, setLocation] = useState(null);

  // Handle image upload and preview
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
      setImagePreview(URL.createObjectURL(file));
    }
  };

  // Capture image from the camera
  const handleCameraCapture = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ video: true });
    const videoElement = document.createElement('video');
    videoElement.srcObject = stream;
    videoElement.play();

    const canvas = document.createElement('canvas');
    const context = canvas.getContext('2d');

    videoElement.addEventListener('canplay', () => {
      canvas.width = videoElement.videoWidth;
      canvas.height = videoElement.videoHeight;
      context.drawImage(videoElement, 0, 0, canvas.width, canvas.height);
      canvas.toBlob((blob) => {
        setImage(blob);
        setImagePreview(canvas.toDataURL('image/jpeg'));
        stream.getTracks().forEach(track => track.stop());
      }, 'image/jpeg');
    });
  };

  // Reset the form
  const resetAnalysis = () => {
    setImage(null);
    setImagePreview(null);
    setImageUrl(null);
    setResult(null);
    setLocation(null);
    fetchLocationCity(); // Fetch location again for new analysis
  };

  // Capture user's city name using Geolocation
  const fetchLocationCity = async () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        async (position) => {
          const lat = position.coords.latitude;
          const lon = position.coords.longitude;

          try {
            const response = await fetch(
              `https://api.bigdatacloud.net/data/reverse-geocode-client?latitude=${lat}&longitude=${lon}&localityLanguage=en`
            );
            const data = await response.json();
            setLocation(data.city || "Unknown City");
          } catch (error) {
            console.error("Error fetching city:", error);
            setLocation("Unknown City");
          }
        },
        (error) => {
          console.error("Error fetching location:", error);
          alert("Unable to fetch location. Please enable location services.");
        }
      );
    } else {
      alert("Geolocation is not supported by your browser.");
    }
  };

  // Upload image to Cloudinary and analyze food
  const analyzeFood = async () => {
    if (!image) {
      alert("Please upload an image first.");
      return;
    }

    if (!location) {
      alert("Fetching location. Please try again in a moment.");
      return;
    }

    try {
      // Upload the image to Cloudinary
      const formData = new FormData();
      formData.append("file", image);
      formData.append("upload_preset", CLOUDINARY_UPLOAD_PRESET);

      const cloudinaryResponse = await axios.post(CLOUDINARY_UPLOAD_URL, formData);
      const cloudinaryUrl = cloudinaryResponse.data.secure_url;
      setImageUrl(cloudinaryUrl);

      // Send the Cloudinary URL and location to FastAPI
      const apiResponse = await axios.post(FAST_API_URL, {
        image_url: cloudinaryUrl,
        location: location,
      });

      const data = apiResponse.data;

      // Extract only the required details from the response
      const isExpired = new Date(data.meta_data.exp_date) < new Date();
      const daysToExpire = isExpired
        ? 0
        : Math.ceil(
            (new Date(data.meta_data.exp_date) - new Date()) /
            (1000 * 60 * 60 * 24)
          );

      setResult({
        isExpired,
        daysToExpire,
      });
    } catch (error) {
      console.error("Error analyzing food:", error);
      setResult({
        isExpired: null,
        daysToExpire: null,
      });
    }
  };

  // Fetch location on component mount
  useEffect(() => {
    fetchLocationCity();
  }, []);

  return (
    <div className="food-container">
      <div className="food-header">
        <h1 className="food-title">AI Food Analyzer</h1>
      </div>
      <p className="food-description">
        Upload or capture food photos to check for spoilage. 
        Our AI ensures quality by detecting signs of freshness or decay.
      </p>
      <div className="upload-container">
        <input className="file-input" type="file" accept="image/*" onChange={handleImageUpload} />
        <button className="analyze-button" onClick={handleCameraCapture}>üì∑ Capture from Camera</button>
        <button className="analyze-button" onClick={analyzeFood}>üîç Analyze</button>
        <button className="reset-button" onClick={resetAnalysis}>üîÑ Reset</button>
      </div>
      {result && (
        <div className="analysis-result">
          <div className="image-preview-container">
            <img className="image-preview-large" src={imagePreview} alt="Analyzed food" />
          </div>
          <div className="result-container">
            {result.isExpired !== null && (
              <>
                <p className="result-text">
                  {result.isExpired
                    ? "‚ö†Ô∏è The food is expired."
                    : `‚úÖ The food is fresh. Days until expiry: ${result.daysToExpire}`}
                </p>
              </>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Food;
